import React, { useState } from "react";
import AddUser from "./components/Users/AddUser";
import ListUser from "./components/Users/ListUser";

function App() {
  const [userList, addUserToList] = useState([]);
  const addUserToListHandler = (user) => {
    userList.push(user);
    addUserToList(userList);
  };

  return (
    <div>
      <AddUser addUserToList={addUserToListHandler}></AddUser>
      <ListUser userList={userList}></ListUser>
    </div>
  );
}

export default App;
