import React, { useState } from "react";
import Card from "../UI/Card";
import Button from "../UI/Button";
import classes from "./AddUser.module.css";

const AddUser = (props) => {
  const [username, setUserName] = useState("");
  const [age, setAge] = useState("");
  const addEventListener = (event) => {
    event.preventDefault();
    if (username && age && age > 0) {
      console.log(username, age);
      setUserName("");
      setAge("");
    }
  };
  const setChangedUserNameHandler = (event) => {
    setUserName(event.target.value);
  };
  const setChangedAgeHandler = (event) => {
    setAge(event.target.value);
  };
  return (
    <Card className={classes.input}>
      <form onSubmit={addEventListener}>
        <label htmlFor="username">UserName</label>
        <input
          id="username"
          type="text"
          value={username}
          onChange={setChangedUserNameHandler}
        ></input>
        <label htmlFor="age">Age (Years)</label>
        <input
          id="age"
          type="number"
          value={age}
          onChange={setChangedAgeHandler}
        ></input>
        <Button type="submit" onClick={addEventListener}>
          Add User
        </Button>
      </form>
    </Card>
  );
};

export default AddUser;
