import React, { useState } from "react";
import Card from "../UI/Card";
import Button from "../UI/Button";
import classes from "./AddUser.module.css";
import ErrorModel from "../UI/ErrorModel";

const AddUser = (props) => {
  const [username, setUserName] = useState("");
  const [age, setAge] = useState("");
  const addUserListener = (event) => {
    event.preventDefault();
    if (username.trim().length !== 0 && age.trim().length !== 0 && +age > 0) {
      console.log(username, age);
      props.onAddUser({ username: username, age: age });
      setUserName("");
      setAge("");
    } else {
      return;
    }
  };
  const setChangedUserNameHandler = (event) => {
    setUserName(event.target.value);
  };
  const setChangedAgeHandler = (event) => {
    setAge(event.target.value);
  };
  return (
    <div>
      <ErrorModel hideErrorModel = {hideErrorModelHandler}title="Look at this"></ErrorModel>
      <Card className={classes.input}>
        <form onSubmit={addUserListener}>
          <label htmlFor="username">UserName</label>
          <input
            id="username"
            type="text"
            value={username}
            onChange={setChangedUserNameHandler}
          ></input>
          <label htmlFor="age">Age (Years)</label>
          <input
            id="age"
            type="number"
            value={age}
            onChange={setChangedAgeHandler}
          ></input>
          <Button type="submit">Add User</Button>
        </form>
      </Card>
    </div>
  );
};

export default AddUser;
