import React, { useRef, useState } from "react";
import Card from "../UI/Card";
import Button from "../UI/Button";
import classes from "./AddUser.module.css";
import ErrorModel from "../UI/ErrorModel";
import Wrapper  from "../Helpers/Wrapper";

const AddUser = (props) => {
  const [username, setUserName] = useState("");
  const [age, setAge] = useState("");
  const usernameRef = useRef();
  const ageRef = useRef();
  const [error, setError] = useState();
  const addUserListener = (event) => {
    event.preventDefault();
    if (username.trim().length !== 0 && age.trim().length !== 0 && +age > 0) {
      console.log(username, age);
      props.onAddUser({ username: username, age: age });
      setUserName("");
      setAge("");
    } else {
      if (age < 1) {
        setError({
          title: "Invalid Age",
          message: "Please set the age above 1",
        });
        return;
      } else if (username.trim().length === 0) {
      }
      setError({
        title: "Invalid username",
        message: "Please set the username",
      });
      return;
    }
  };
  const setChangedUserNameHandler = (event) => {
    setUserName(event.target.value);
  };
  const setChangedAgeHandler = (event) => {
    setAge(event.target.value);
  };
  const errorHandler = () => {
    setError();
  };
  return (
    <Wrapper>
      {error && (
        <ErrorModel
        onConfirm={errorHandler}
          title={error.title}
          message={error.message}
        ></ErrorModel>
      )}

      <Card className={classes.input}>
        <form onSubmit={addUserListener}>
          <label htmlFor="username">UserName</label>
          <input
            id="username"
            type="text"
            value={username}
            onChange={setChangedUserNameHandler}
            ref={usernameRef}
          ></input>
          <label htmlFor="age">Age (Years)</label>
          <input
            id="age"
            type="number"
            value={age}
            onChange={setChangedAgeHandler}
            ref={ageRef}
          ></input>
          <Button type="submit">Add User</Button>
        </form>
      </Card>
    </Wrapper>
  );
};

export default AddUser;
